import React from 'react';
import { Text, View, TouchableOpacity, TextInput, Image, StyleSheet, KeyboardAvoidingView ,ToastAndroid, Alert } from 'react-native';
import * as Permissions from 'expo-permissions';
import { BarCodeScanner } from 'expo-barcode-scanner';
import * as firebase from 'firebase'
import db from '../config.js'


export default class TransactionScreen extends React.Component {

    constructor(){
      super();

      this.state = {
        hasCameraPermissions: null,
        scanned: false,
        scannedBookId: '',
        scannedStudentId:'',
        buttonState: 'normal',
        transactionMessage: ''
      }; 
    }


    getCameraPermissions = async (id) =>{
      const {status} = await Permissions.askAsync(Permissions.CAMERA);

      this.setState({
        /*status === "granted" is true when user has granted permission
          status === "granted" is false when user has not granted the permission
        */
        hasCameraPermissions: status === "granted",
        buttonState: id,
        scanned: false
      });
    }


    handleBarCodeScanned = async({type, data})=>{
      const {buttonState} = this.state;

      if(buttonState==="BookId"){
        this.setState({
          scanned: true,
          scannedBookId: data,
          buttonState: 'normal'
        });
      }
      else if(buttonState==="StudentId"){
        this.setState({
          scanned: true,
          scannedStudentId: data,
          buttonState: 'normal'
        });
      }

    }


    initiateBookIssue = async()=>{
      //add a transaction
      db.collection("transactions").add({
        'studentId': this.state.scannedStudentId,
        'bookId' : this.state.scannedBookId,
        'date' : firebase.firestore.Timestamp.now().toDate(),
        'transactionType': "Issue"
      });

      //change book status
      db.collection("books").doc(this.state.scannedBookId).update({
        'bookAvailability': false
      });

      //change number  of issued books for student
      db.collection("students").doc(this.state.scannedStudentId).update({
        'numberOfBooksIssued': firebase.firestore.FieldValue.increment(1)
      });

      this.setState({
        scannedBookId: "",
        scannedStudentId: ""
      });

    }


    initiateBookReturn = async()=>{
      //add a transaction
      db.collection("transactions").add({
        'studentId': this.state.scannedStudentId,
        'bookId' : this.state.scannedBookId,
        'date' : firebase.firestore.Timestamp.now().toDate(),
        'transactionType': "Return"
      });

      //change book status
      db.collection("books").doc(this.state.scannedBookId).update({
        'bookAvailability': true
      });

      //change number  of issued books for student
      db.collection("students").doc(this.state.scannedStudentId).update({
        'numberOfBooksIssued': firebase.firestore.FieldValue.increment(-1)
      });

      this.setState({
        scannedStudentId: "",
        scannedBookId: ""
      });

    };


    checkBookEligibility = async ()=>{
      const bookRef = await db
      .collection("books")
      .where("bookId", "==", this.state.scannedBookId)
      .get();

      var transactionType = "";

      if(bookRef.docs.length === 0){
        transactionType = false;
      }
      else{
        bookRef.docs.map( doc =>{
          var book = doc.data();

          if(book.bookAvailability){
            transactionType = "issue";
          }
          else{
            transactionType = "return";
          }

        });
      }

      return transactionType;

    }


    checkStudentEligibilityForBookIssue = async ()=>{
      const studentRef = await db
      .collection("students")
      .where("studentId","==",this.state.scannedStudentId)
      .get();

      var isStudentEligible = "";

      if(studentRef.docs.length == 0){
        this.setState({
          scannedStudentId: "",
          scannedBookId: ""
        });
        isStudentEligible = false;
        Alert.alert("Student Id does not exist in the database");
      }
      else{
        studentRef.docs.map(doc =>{
          var student = doc.data();

          if(student.numberOfBooksIssued < 2){
            isStudentEligible = true;
          }
          else{
            isStudentEligible = false;
            Alert.alert("Student has already issued 2 books");
            this.setState({
              scannedStudentId: "",
              scannedBookId: ""
            });
          }

        });
      }

      return isStudentEligible;
    };


    checkStudentEligibilityForBookReturn = async ()=>{
      const transactionRef = await db
      .collection("transactions")
      .where("bookId", "==", this.state.scannedBookId)
      .limit(1)
      .get();

      var isStudentEligible = "";

      transactionRef.docs.map(doc =>{
        var lastBookTransaction = doc.data();

        if(lastBookTransaction.studentId === this.state.scannedStudentId){
          isStudentEligible = true;
        }
        else{
          isStudentEligible = false;
          Alert.alert("Book wasn't issued by this student");
          this.setState({
            scannedBookId: "",
            scannedStudentId: ""
          });
        }

      });

      return isStudentEligible;
    };


    handleTransaction = async()=>{
     /* var transactionMessage
      db.collection("books").doc(this.state.scannedBookId).get()
      .then((doc)=>{
            var book = doc.data()
            if(book.bookAvailability){
                this.initiateBookIssue();       
                transactionMessage = "Book Issued"
                ToastAndroid.show(transactionMessage, ToastAndroid.SHORT);
                // Alert.alert(transactionMessage)
            }
            else{
                this.initiateBookReturn();
                transactionMessage = "Book Returned"
                ToastAndroid.show(transactionMessage, ToastAndroid.SHORT);
                // Alert.alert(transactionMessage)
            }
      })

      this.setState({
        transactionMessage: transactionMessage
      })*/


      var transactionType = await this.checkBookEligibility();

      if(!transactionType){
        Alert.alert("Book doesn't exist in the library datatbase!");
        ToastAndroid.show("Book is in state of non-existance", ToastAndroid.SHORT);
        this.setState({
          scannedStudentId: "",
          scannedBookId: ""
        });
      }
      else if(transactionType === "Issue"){
        var isStudentEliible = await this.checkStudentEligibilityForBookIssue();
        if(isStudentEliible){
          this.initiateBookIssue();
          Alert.alert("Book Issued to the Student");
        }
      }
      else{
        var isStudentEliible = await this.checkStudentEligibilityForBookReturn();
        if(isStudentEliible){
          this.initiateBookReturn();
          Alert.alert("Book Returned to the Library");
        }
      }

    };


    render() {
      const hasCameraPermissions = this.state.hasCameraPermissions;
      const scanned = this.state.scanned;
      const buttonState = this.state.buttonState;

      if (buttonState !== "normal" && hasCameraPermissions){
        return(
          <BarCodeScanner
            onBarCodeScanned={scanned ? undefined : this.handleBarCodeScanned}
            style={StyleSheet.absoluteFillObject}
          />
        );
      }

      else if (buttonState === "normal"){
        return(
          <KeyboardAvoidingView  style={styles.container} behavior="padding" enabled>
            <View>
              <Image
                source={require("../assets/booklogo.jpg")}
                style={{width:200, height: 200}}/>
              <Text style={{textAlign: 'center', fontSize: 30}}>Wily</Text>
            </View>
            <View style={styles.inputView}>
            <TextInput 
              style={styles.inputBox}
              placeholder="Book Id"
              onChangeText={text =>this.setState({scannedBookId:text})}
              value={this.state.scannedBookId}/>
            <TouchableOpacity 
              style={styles.scanButton}
              onPress={()=>{
                this.getCameraPermissions("BookId")
              }}>
              <Text style={styles.buttonText}>Scan</Text>
            </TouchableOpacity>
            </View>

            <View style={styles.inputView}>
            <TextInput 
              style={styles.inputBox}
              placeholder="Student Id"
              onChangeText ={text => this.setState({scannedStudentId:text})}
              value={this.state.scannedStudentId}/>
            <TouchableOpacity 
              style={styles.scanButton}
              onPress={()=>{
                this.getCameraPermissions("StudentId")
              }}>
              <Text style={styles.buttonText}>Scan</Text>
            </TouchableOpacity>
            </View>
            <TouchableOpacity
              style={styles.submitButton}
              onPress={async()=>{
                var transactionMessage = this.handleTransaction();
                this.setState(
                  {scannedBookId:'',
                   scannedStudentId:''})
              }}>
          <Text style={styles.submitButtonText}>Submit</Text>
            </TouchableOpacity>
          </KeyboardAvoidingView>
